import React,{useState} from 'react';
import ToDoList from "./ToDoList";
import './index.css';



const App=() =>{
   const [inputList, setInputList] = useState("");
   const [items,setItems] = useState([]);

  const itemEvent = (event)=>{
    setInputList(event.target.value);
  }
  const listOfItem =()=>{
    if(inputList===''){
      // alert("ENTER AN ITEM");
      return undefined;
    }
    else{
      setItems((oldItems)=>{
      return [...oldItems,inputList];
      });
      setInputList("");
    };
  }
  const deleteItem=(id)=>{
    setItems((oldItems)=>{
      return oldItems.filter((arrElem,index)=>{
         return index !== id;
      })
    })
  }
  return(
    <>
       <div className='main-div'>
           <div className='center-div'>
              <br/>
                <h1> ToDoList</h1>
              <br/> 
              <div className='items'>
                <input type="text" placeholder='Add an item' onChange={itemEvent} value={inputList}/>
                <i className="fa fa-plus add-btn"onClick={listOfItem}></i>
              </div>
              <ul>
                {items.map((itemVal,index)=>{
                  return <ToDoList key={index} id={index} onSelect={deleteItem} text={itemVal}/>
                })}
              </ul>
              <div className="button_add">
              <button className="btn2">Remove all</button>
              </div>
           </div>
       </div>

    </>
  );
}

export default App;
