import React,{useState,useEffect} from 'react';
import ToDoList from "./ToDoList";
import './index.css';

const getLocalData=()=>{
  const list = localStorage.getItem("todoapp");
  if(list){
    return JSON.parse(list);
  }
  else{
    return [];
  }
};

const App=() =>{
   const [inputList, setInputList] = useState("");
   const [items,setItems] = useState(getLocalData());
   const [isEditItem,setIsEditItem] = useState("");
   const [toggleBtn,setToggleBtn] = useState(false);
  const itemEvent = (event)=>{
    setInputList(event.target.value);
  }
  const listOfItem =()=>{
    if(inputList===''){
      // alert("ENTER AN ITEM");
      return undefined;
    }
    else if(inputList && toggleBtn){
       setItems(
         items.map((currElem)=>{
           console.log(currElem.index);
         })
       );

    }
    else{
      setItems((oldItems)=>{
      return [...oldItems,inputList];
      });
      setInputList("");
    };
  }
  const editItem = (id) =>{
     setInputList(items[id]);
     setIsEditItem(id);
     setToggleBtn(true);
  }
  const deleteItem=(id)=>{
    setItems((oldItems)=>{
      return oldItems.filter((arrElem,index)=>{
         return index !== id;
      })
    })
  }
  const deleteAll = () =>{
    setItems([]);
  }
  useEffect(()=>{localStorage.setItem("todoapp",JSON.stringify(items))});

  return(
    <>
       <div className='main-div'>
           <div className='center-div'>
              <br/>
                <h1> ToDoList</h1>
              <br/> 
              <div className='items'>
                <input type="text" placeholder='Add an item' onChange={itemEvent} value={inputList}/>
                {toggleBtn ? (<i className="fas fa-edit add-btn"  id="i-edit" onClick={listOfItem} ></i>)
                :(<i className="fa fa-plus add-btn" onClick={listOfItem}></i>)}
              </div>
              <ul>
                {items.map((itemVal,index)=>{
                  return <ToDoList key={index} id={index} onSelect={deleteItem} onClick={editItem} text={itemVal}/>
                })}
              </ul>
              <div className="button_add">
              <button className="btn2" onClick={deleteAll}>Remove all</button>
              </div>
           </div>
       </div>

    </>
  );
}

export default App;
